await client.docgen.createDocgenBatchV2025R0({
  file: new FileReferenceV2025R0({ id: uploadedFileDocx.id }),
  inputSource: 'api',
  destinationFolder: new DocGenBatchCreateRequestV2025R0DestinationFolderField({
    id: folder.id,
  }),
  outputType: 'pdf',
  documentGenerationData: [
    {
      generatedFileName: 'test',
      userInput: { ['abc']: 'xyz' },
    } satisfies DocGenDocumentGenerationDataV2025R0,
  ],
} satisfies DocGenBatchCreateRequestV2025R0);

await client.hubs.getHubsV2025R0({
  scope: 'all',
  sort: 'name',
  direction: 'ASC' as GetHubsV2025R0QueryParamsDirectionField,
} satisfies GetHubsV2025R0QueryParams);

await client.hubs.createHubV2025R0({
  title: hubTitle,
  description: hubDescription,
} satisfies HubCreateRequestV2025R0);

await client.hubs.getEnterpriseHubsV2025R0({
  sort: 'name',
  direction: 'ASC' as GetEnterpriseHubsV2025R0QueryParamsDirectionField,
} satisfies GetEnterpriseHubsV2025R0QueryParams);

await client.hubs.updateHubByIdV2025R0(hubId, {
  title: newHubTitle,
  description: newHubDescription,
} satisfies HubUpdateRequestV2025R0);

await client.hubs.copyHubV2025R0(createdHub.id, {
  title: copiedHubTitle,
  description: copiedHubDescription,
} satisfies HubCopyRequestV2025R0);

await client.hubCollaborations.createHubCollaborationV2025R0({
  hub: new HubCollaborationCreateRequestV2025R0HubField({ id: hub.id }),
  accessibleBy: {
    type: 'user',
    id: user.id,
  } satisfies HubCollaborationCreateRequestV2025R0AccessibleByField,
  role: 'viewer',
} satisfies HubCollaborationCreateRequestV2025R0);

await client.hubItems.getHubItemsV2025R0({
  hubId: createdHub.id,
} satisfies GetHubItemsV2025R0QueryParams);

await client.hubItems.manageHubItemsV2025R0(createdHub.id, {
  operations: [
    {
      action: 'add' as HubItemOperationV2025R0ActionField,
      item: new FolderReferenceV2025R0({ id: folder.id }),
    } satisfies HubItemOperationV2025R0,
  ],
} satisfies HubItemsManageRequestV2025R0);

await client.shieldLists.createShieldListV2025R0({
  name: shieldListCountryName,
  description: 'A list of things that are shielded',
  content: new ShieldListContentCountryV2025R0({
    type: 'country' as ShieldListContentCountryV2025R0TypeField,
    countryCodes: ['US', 'PL'],
  }),
} satisfies ShieldListsCreateV2025R0);

await client.shieldLists.updateShieldListByIdV2025R0(shieldListCountry.id, {
  name: shieldListCountryName,
  description: 'Updated description',
  content: new ShieldListContentCountryV2025R0({
    type: 'country' as ShieldListContentCountryV2025R0TypeField,
    countryCodes: ['US'],
  }),
} satisfies ShieldListsUpdateV2025R0);

await client.archives.createArchiveV2025R0({
  name: archiveName,
  description: archiveDescription,
} satisfies CreateArchiveV2025R0RequestBody);

await client.archives.updateArchiveByIdV2025R0(archive.id, {
  requestBody: {
    name: newArchiveName,
    description: newArchiveDescription,
  } satisfies UpdateArchiveByIdV2025R0RequestBody,
} satisfies UpdateArchiveByIdV2025R0OptionalsInput);

await client.externalUsers.submitJobToDeleteExternalUsersV2025R0({
  externalUsers: [
    new UserReferenceV2025R0({ id: getEnvVar('BOX_EXTERNAL_USER_ID') }),
  ],
} satisfies ExternalUsersSubmitDeleteJobRequestV2025R0);
